import React, { FC, memo, useState, useCallback } from "react";
import styled from "styled-components";
import { useQuery } from "@apollo/client";
import { recommandUsersQuery } from "../../graphql/user/query";
import Avatar from "../common/Avatar";
import HoverUser from "../user/HoverUser";
import FollowButton from "../common/FollowButton";
import { useVssState } from "../../context";

const Container = styled.div`
  display: flex;
  flex-direction: column;
`;

const Column = styled.div`
  height: 50px;
  display: flex;
  justfiy-content: flex-start;
  align-items: center;
  position: relative;
  padding: 10px;
`;

const AvatarWrapper = styled.div`
  width: 50px;
`;

const NicknameWrapper = styled.div`
  width: 140px;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
`;

const FollowWrapper = styled.div`
  width: 80px;
`;

const RecommandUserList: FC = () => {
  const { userId } = useVssState();
  const [isShowUser, setIsShowUser] = useState<boolean>(false);

  const { data } = useQuery(recommandUsersQuery);

  console.log(data);

  const handleClickAvatar = useCallback(() => {
    setIsShowUser(!isShowUser);
  }, [isShowUser]);

  const mapToRecommandUsers = () =>
    data.getRecommandUsers.map(({ id, avatar, nickname, followedBy }) => {
      const isFollowing = followedBy.some(v => v.id === userId);
      return (
        <Column key={id}>
          <AvatarWrapper>
            <Avatar size="30" src={avatar.url} onClick={handleClickAvatar} />
          </AvatarWrapper>

          <NicknameWrapper>{nickname}</NicknameWrapper>
          <FollowWrapper>
            <FollowButton isFollowing={isFollowing} userId={id} />
          </FollowWrapper>

          {isShowUser && <HoverUser userId={id} />}
        </Column>
      );
    });

  return <Container>{mapToRecommandUsers()}</Container>;
};

export default memo(RecommandUserList);
