import React, { useReducer, useContext, createContext, Dispatch } from "react";
import { Avatar } from "../interfaces";

type ActivePost = {
  id: string;
  title: string;
  description: string;
  status: string;
  url: string;
};

type ActiveNotice = {
  action: string;
  actionText: string;
  title: string;
  description: string;
  noticeId: string;
};

// type for state
type State = {
  id: string | null;
  nickname: string | null;
  email: string | null;
  avatar: Avatar | null;
  isMaster: boolean;
  isShowNoticeModal: boolean;
  isShowAddPostModal: boolean;
  isShowSearchBar: boolean;
  isShowLoginModal: boolean;
  activePost: ActivePost;
  activeNotice: ActiveNotice;
};

export const SET_ME = "SET_ME";
export const SHOW_NOTICE_MODAL = "SHOW_NOTICE_MODAL";
export const HIDE_NOTICE_MODAL = "HIDE_NOTICE_MODAL";
export const SHOW_POST_MODAL = "SHOW_POST_MODAL";
export const HIDE_POST_MODAL = "HIDE_POST_MODAL";
export const SET_LOGIN_MODAL = "SET_LOGIN_MODAL";
export const ADD_ROOM = "ADD_ROOM";
export const REMOVE_ROOM = "REMOVE_ROOM";
export const SET_SEARCH_BAR = "SET_SEARCH_BAR";

// type for action
type Action =
  | {
      type: "SET_ME";
      id: string;
      nickname: string;
      email: string;
      avatar: Avatar | null;
      isMaster: boolean;
    }
  | {
      type: "SHOW_NOTICE_MODAL";
      payload: boolean;
      action: string;
      actionText: string;
      title: string;
      description: string;
      noticeId: string;
    }
  | { type: "HIDE_NOTICE_MODAL" }
  | {
      type: "SHOW_POST_MODAL";
      id?: string;
      title?: string;
      description?: string;
      status?: string;
      url?: string;
    }
  | { type: "HIDE_POST_MODAL" }
  | { type: "ADD_ROOM"; payload: string }
  | { type: "REMOVE_ROOM"; payload: string }
  | { type: "SET_SEARCH_BAR"; payload: boolean }
  | { type: "SET_LOGIN_MODAL"; payload: boolean };

// type for dispatch
type VssDispatch = Dispatch<Action>;

// create context
const VssContext = createContext<State | null>(null);
const VssDispatchContext = createContext<VssDispatch | null>(null);

// define reducer
function reducer(state: any, action: any) {
  switch (action.type) {
    case "SET_ME":
      return {
        ...state,
        id: action.id,
        nickname: action.nickname,
        email: action.email,
        avatar: action.avatar,
        isMaster: action.isMaster
      };
    case "SHOW_NOTICE_MODAL":
      return {
        ...state,
        isShowNoticeModal: action.payload
      };
    case "HIDE_NOTICE_MODAL":
      return {
        ...state,
        isShowNoticeModal: false
      };
    case "SHOW_POST_MODAL":
      return {
        ...state,
        isShowAddPostModal: true,
        activePost: {
          id: action.id,
          title: action.title,
          description: action.description,
          status: action.status,
          url: action.url
        }
      };
    case "HIDE_POST_MODAL":
      return {
        ...state,
        isShowAddPostModal: false,
        activePost: {
          id: "",
          title: "",
          description: "",
          status: "",
          url: ""
        }
      };
    case "ADD_ROOM":
      return {
        ...state,
        activeRoom: state.activeRoom.concat([action.payload] as any)
      };
    case "REMOVE_ROOM":
      return {
        ...state,
        activeRoom: state.activeRoom.filter(v => v.id !== action.payload)
      };
    case "SET_SEARCH_BAR":
      return {
        ...state,
        isShowSearchBar: action.payload
      };
    case "SET_LOGIN_MODAL":
      return {
        ...state,
        isShowLoginModal: action.payload
      };
    default:
      return { ...state };
  }
}

const initialState: State = {
  id: null,
  nickname: null,
  email: null,
  avatar: null,
  isMaster: false,
  isShowNoticeModal: false,
  isShowAddPostModal: false,
  isShowSearchBar: false,
  isShowLoginModal: false,
  activePost: {
    id: "",
    title: "",
    description: "",
    status: "",
    url: ""
  }
};

export function VssProvider({ children }) {
  const [state, dispatch] = useReducer(reducer, initialState);

  return (
    <VssContext.Provider value={state}>
      <VssDispatchContext.Provider value={dispatch}>
        {children}
      </VssDispatchContext.Provider>
    </VssContext.Provider>
  );
}

// for hooks
export function useVssState() {
  const state = useContext(VssContext);
  if (!state) throw new Error("not found provider");
  return state;
}

export function useVssDispatch() {
  const dispatch = useContext(VssDispatchContext);
  if (!dispatch) throw new Error("not found provider");
  return dispatch;
}
