import React from "react";
import { NextPage, GetServerSideProps } from "next";
import { useQuery } from "@apollo/client";
import { initializeApollo } from "../lib/apollo";
import { searchQuery } from "../graphql/page/query/search";
import Layout from "../components/common/Layout";
import { postsQuery } from "../graphql/post/query";
// import SearchPost from "../components/search/SearchPostContainer";

const Search: NextPage = props => {
  // const { data: posts, loading, fetchMore, networkStatus } = useQuery(
  //   postsQuery,
  //   {
  //     variables: {
  //       first: 10,
  //       keyword: query.keyword
  //     },
  //     notifyOnNetworkStatusChange: true
  //   }
  // );
  return (
    <Layout>
      <h1>test</h1>
    </Layout>
  );
};

export const getServerSideProps: GetServerSideProps = async ({ query }) => {
  const { keyword } = query;
  const apolloClient = initializeApollo();
  await apolloClient.query({
    query: searchQuery,
    variables: {
      first: 10,
      keyword
    }
  });

  return {
    query: {
      keyword
    },
    props: {
      initialApolloState: apolloClient.cache.extract()
    }
  };
};

export default Search;
