import React, { useCallback, useState, FC, useRef, FormEvent } from "react";
import { useMutation } from "@apollo/client";
import axios from "axios";
import { addPostMutation } from "../../graphql/post/mutation/add";
import { updatePostMutation } from "../../graphql/post/mutation/update";
import { removePostMutation } from "../../graphql/post/mutation/remove";
import SetPostPresenter from "./SetPostPresenter";
import { useInput } from "../../hooks";
import { useVssDispatch, SET_POST_MODAL } from "../../context";

type Props = {
  title?: string;
  description?: string;
  status?: string;
  postId?: string;
  url?: string;
};

const SetPostContainer: FC<Props> = ({
  title,
  description,
  status,
  postId,
  url
}) => {
  const dispatch = useVssDispatch();

  const [header, setHeader] = useState(postId ? "수정" : "영상 업로드");

  const fileEl = useRef<HTMLInputElement>(null);

  const modalTitle = useInput(title || "");
  const modalDescription = useInput(description || "");
  const modalStatus = useInput(status || "PUBLIC");
  const [progress, setProgress] = useState<number>(postId ? 100 : 0);
  const [file, setFile] = useState<string>(url ? url : "");

  const [set, { loading: setPostLoading }] = useMutation(
    postId ? updatePostMutation : addPostMutation
  );

  const [remove, { loading: removePostLoading }] = useMutation(
    removePostMutation
  );

  const handleClickFile = useCallback(() => {
    const node = fileEl.current;
    if (node) {
      node.click();
    }
  }, []);

  const handleChangeFile = useCallback(async e => {
    if (!e.target.value) return; // cancel select file

    const { files } = e.target;

    const formData = new FormData();
    formData.append("file", files[0]);

    const response = await axios.post(
      `${process.env.BACKEND_API_PATH}/api/upload`,
      formData,
      {
        onUploadProgress: ({ lengthComputable, loaded, total }) => {
          if (lengthComputable) {
            setProgress(Math.floor((loaded / total) * 100));
          }
        }
      }
    );
    if (response.data) {
      setFile(response.data);
      setHeader("등록");
    }
  }, []);

  const handleDelete = useCallback(async () => {
    if (removePostLoading) {
      return alert("요청 중입니다. 잠시만 기다려주세요.");
    }

    const tf = confirm("포스트를 삭제하시겠어요?");

    if (tf) {
      const {
        data: { deletePost }
      } = await remove({
        variables: {
          postId
        }
      });
      if (deletePost) {
        alert("포스트가 삭제되었습니다.");
        window.location.reload();
      }
    }
  }, [removePostLoading]);

  const handleSubmit = useCallback(
    async (e: FormEvent<HTMLFormElement>) => {
      e.preventDefault();
      if (setPostLoading) {
        return alert("요청 중입니다. 잠시만 기다려주세요.");
      }

      const tf = confirm(
        `입력한 내용으로 ${postId ? "수정" : "등록"}하시겠어요?`
      );

      if (tf) {
        try {
          const {
            data: { addPost, updatePost }
          } = await set({
            variables: {
              postId,
              title: modalTitle.value,
              description: modalDescription.value,
              status: modalStatus.value,
              file
            }
          });
          if (addPost || updatePost) {
            alert(`포스트가 ${postId ? "수정" : "등록"} 되었습니다.`);
            location.reload();
          }
        } catch (error) {
          const { message } = JSON.parse(error.message);
          alert(message);
        }
      }
    },
    [
      modalTitle.value,
      modalDescription.value,
      modalStatus.value,
      file,
      setPostLoading
    ]
  );

  const handleClose = useCallback(() => {
    dispatch({
      type: SET_POST_MODAL,
      payload: false
    });
  }, []);

  return (
    <SetPostPresenter
      loading={setPostLoading}
      header={header}
      title={modalTitle}
      description={modalDescription}
      status={modalStatus}
      progress={progress}
      file={file}
      fileEl={fileEl}
      onClickFile={handleClickFile}
      onChangeFile={handleChangeFile}
      onClose={handleClose}
      onDelete={handleDelete}
      onSubmit={handleSubmit}
    />
  );
};

export default SetPostContainer;
