import React, { useState, FC, useCallback } from "react";
import styled from "styled-components";
import { Modal } from "react-bootstrap";
import SignIn from "../auth/SignInContainer";
import SignUp from "../auth/SignUpContainer";
import { StateChanger } from "../common/Form";
import { useVssDispatch, SET_LOGIN_MODAL } from "../../context";
import { ModalHeader, ModalBody } from "../common/Modal";

const Link = styled.span`
  color: ${props => props.theme.blueColor};
  cursor: pointer;
`;

const Auth: FC = () => {
  const dispatch = useVssDispatch();
  const [action, setAction] = useState<string>("login");

  const handleClose = useCallback(() => {
    dispatch({
      type: SET_LOGIN_MODAL,
      payload: false
    });
  }, []);

  return (
    <Modal onHide={handleClose} show animation={false}>
      <ModalHeader closeButton>
        <div>{action === "login" ? "로그인" : "회원가입"}</div>
      </ModalHeader>
      <ModalBody>
        {action === "signup" ? <SignUp setAction={setAction} /> : <SignIn />}
        <StateChanger>
          {action === "login" && (
            <div>
              계정이 없다면&nbsp;
              <Link onClick={() => setAction("signup")}>회원가입</Link>
            </div>
          )}
          {action === "signup" && (
            <div>
              계정이 있다면&nbsp;
              <Link onClick={() => setAction("login")}>로그인</Link>
            </div>
          )}
        </StateChanger>
      </ModalBody>
    </Modal>
  );
};

export default Auth;
