import { useRouter } from "next/router";
import React, { FC, Fragment } from "react";
import styled from "styled-components";
import SearchUserItem from "../user/SearchUserItem";
import Subject from "../common/Subject";
import NoData from "../common/NoData";

const Container = styled.div`
  width: 100%;
  display: flex;
  height: 230px;
  flex-direction: row;
  gap: 15px;
  margin-bottom: 10px;

  ${props => props.theme.media.phone} {
    flex-direction: column;
    height: auto;
  }
`;

interface Props {
  users: any;
}

const SearchUser: FC<Props> = ({ users }) => {
  const router = useRouter();

  return (
    <>
      <Subject>"{router.query.keyword}"에 대한 검색결과</Subject>
      {users.length === 0 && <Subject>추천 사용자</Subject>}
      <Container>
        {users.length > 0 ? (
          users.map(user => <SearchUserItem key={user.id} {...user} />)
        ) : (
          <NoData>검색 결과가 없습니다.</NoData>
        )}
      </Container>
    </>
  );
};

export default SearchUser;
