import React from "react";
import { NextPage } from "next";
import { useRouter } from "next/router";
import { useQuery } from "@apollo/client";
import { initializeApollo } from "../lib/apollo";
import { usersQuery } from "../graphql/user/query";
import { meQuery } from "../graphql/auth/query/me";
import { useVssDispatch, SET_ME } from "../context";
import Layout from "../components/common/Layout";
import Section from "../components/common/Section";
import SearchUser from "../components/search/SearchUser";
import SearchPost from "../components/search/SearchPostContainer";
import isBrowser from "../lib/isBrowser";
import Subject from "../components/common/Subject";

const Search: NextPage<any> = ({ users }) => {
  const router = useRouter();
  const dispatch = useVssDispatch();
  useQuery(meQuery, {
    skip: !isBrowser,
    onCompleted: ({ getMyProfile }) => {
      const { id, nickname, email, avatar, isMaster } = getMyProfile;
      dispatch({
        type: SET_ME,
        userId: id,
        nickname,
        email,
        avatar,
        isMaster
      });
    }
  });
  return (
    <Layout title="검색결과">
      <Section>
        <Subject>"{router.query.keyword}"의 검색결과</Subject>
        {users.length > 0 && <SearchUser users={users} />}
        <SearchPost />
      </Section>
    </Layout>
  );
};

Search.getInitialProps = async context => {
  const { keyword } = context.query;
  const apolloClient = initializeApollo();
  const { data } = await apolloClient.query({
    query: usersQuery,
    variables: {
      first: 3,
      keyword
    }
  });

  return {
    users: data.getUsers
  };
};

export default Search;
