import React, { Fragment, FC } from "react";
import styled from "styled-components";
import { Modal, Button, ProgressBar } from "react-bootstrap";
import Input from "../common/Input";
import { Label } from "../common/Form";
import { UseInputProps } from "../../hooks";
import Loader from "../common/Loader";
import { Upload } from "../icon";

const UploadWrapper = styled.div`
  ${props => props.theme.whiteBox};
  background: ${props => props.theme.bgColor};
  width: 100%;
  height: 300px;
  cursor: pointer;
  padding: 10px;
  display: flex;
  justify-content: center;
  align-items: center;

  & > div {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    text-align: center;
  }
`;

type Props = {
  loading: boolean;
  header: string;
  title: UseInputProps;
  description: UseInputProps;
  status: UseInputProps;
  progress: number;
  fileEl: any;
  onClickFile: any;
  onChangeFile: any;
  onClose: any;
  onSubmit: any;
};

const SetNoticePresenter: FC<Props> = ({
  loading,
  header,
  title,
  description,
  status,
  progress,
  fileEl,
  onClickFile,
  onChangeFile,
  onClose,
  onSubmit
}) => (
  <Fragment>
    {loading && <Loader />}
    <Modal onHide={onClose} animation={true} show>
      <Modal.Header closeButton>
        <Modal.Title>{header}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <UploadWrapper onClick={onClickFile}>
          <div>
            {progress > 0 && (
              <ProgressBar now={progress} label={`${progress}%`} />
            )}
            {progress === 0 && <Upload style={{ width: 30, height: 30 }} />}
            {progress === 0 && <h1>영상을 업로드하려면 클릭하세요.</h1>}
          </div>

          <input
            type="file"
            onChange={onChangeFile}
            ref={fileEl}
            hidden
            accept="video/*"
          />
        </UploadWrapper>
      </Modal.Body>
      <Modal.Footer></Modal.Footer>
    </Modal>
  </Fragment>
);

export default SetNoticePresenter;
