import React, { FC } from "react";
import styled from "styled-components";
import { Avatar } from "../../interfaces";

const Container = styled.div`
  display: flex;
  flex-direction: column;
`;

interface Props {
  id: string;
  nickname: string;
  avatar: Avatar;
  followedBy: any;
  following: any;
  posts: any;
}

const SearchUserItem: FC<Props> = ({
  id,
  nickname,
  avatar,
  followedBy,
  following,
  posts
}) => (
  <Container>
    <Background src={avatar.url} />
    <Column>
      <div>{nickname}</div>
      <div>
        {MyUserId && userId !== MyUserId && (
          <FollowButton userId={userId} isFollowing={isFollowing} />
        )}
      </div>
    </Column>
    <Column>
      <span className="info">포스트: {posts.length}</span>
      <span className="info">팔로워: {followedBy.length}</span>
      <span className="info">팔로잉: {following.length}</span>
    </Column>
  </Container>
);

export default SearchUserItem;
