import { useRouter } from "next/router";
import React, { useState, useCallback, useEffect } from "react";
import styled from "styled-components";
import searchOptions from "./search_options.json";

const Container = styled.div`
  width: 100%;
  margin: 0 auto;
  margin-top: 10px;
  margin-bottom: 20px;
`;

const FilterWrapper = styled.span`
  ${props => props.theme.whiteBox};
  display: inline-block;
  padding: 10px;

  & + & {
    margin-left: 10px;
  }
`;

const sortAndFilter = searchOptions.sort.concat(searchOptions.filter);

const setTag = (keyword: any) =>
  keyword.map((v, idx) => {
    let query;
    if (idx === 0) {
      query = "keyword";
    } else if (idx === 1) {
      query = "sort";
    } else {
      query = "filter";
    }
    return {
      query,
      value: v,
      text:
        query !== "keyword"
          ? sortAndFilter.filter(v2 => v2.value === v)[0].text
          : ""
    };
  });

const SearchTag = () => {
  const router = useRouter();
  const [filters, setFilters] = useState<any>(setTag(router.query.keyword));

  const handleRemove = useCallback(
    text => {
      const nextFilters = filters.filter(v => v.text !== text);
      setFilters(nextFilters);
      const keyword = filters.find(v => v.query === "keyword");
      const sort = filters.find(v => v.query === "sort");
      const filter = filters.filter(v => v.query === "filter");

      let url = `/search/${keyword.value}`;
      if (sort) {
        url = `${url}/${sort.value}`;
      }
      if (filter) {
        url = `${url}/${filter.map(v => v.value).join("/")}`;
      }
      console.log(url);

      // router.push(url);
    },
    [filters, router.query]
  );

  useEffect(() => {
    setFilters(setTag(router.query.keyword));
  }, [router.query.keyword]);

  return (
    <Container>
      {filters
        .filter(v => v.text)
        .map(v => (
          <FilterWrapper key={v.value}>
            {v.text}
            <span
              aria-hidden="true"
              style={{ marginLeft: 10, cursor: "pointer" }}
              onClick={() => handleRemove(v.text)}
            >
              Ã—
            </span>
          </FilterWrapper>
        ))}
    </Container>
  );
};

export default SearchTag;
