import React, { FC } from "react";
import styled from "styled-components";
import { Avatar } from "../../interfaces";

const Container = styled.div`
  display: flex;
  flex-direction: column;
`;

interface Props {
  id: string;
  nickname: string;
  avatar: Avatar;
  followedBy: any;
  following: any;
  posts: any;
}

const SearchUserItem: FC<Props> = ({
  id,
  nickname,
  avatar,
  followedBy,
  following,
  posts
}) => (
