import { useRouter } from "next/router";
import React, { FC, Fragment } from "react";
import { useQuery } from "@apollo/client";
import { usersQuery, recommandUsersQuery } from "../../graphql/user/query";
import SearchUserPresenter from "./SearchUserPresenter";

const SearchUserContainer: FC = () => {
  const router = useRouter();
  const keyword = decodeURIComponent(router.query.keyword as string);

  const { data: users, loading: usersLoading } = useQuery(usersQuery, {
    variables: {
      keyword,
      first: 10
    },
    ssr: false
  });

  const { data: recommandUsers, loading: recommandUsersLoading } = useQuery(
    recommandUsersQuery,
    {
      ssr: false
    }
  );

  if ((!users && usersLoading) || (!recommandUsers && recommandUsersLoading)) {
    return <Fragment />;
  }

  return (
    <SearchUserPresenter
      users={users.getUsers}
      recommandUsers={recommandUsers.getRecommandUsers}
      keyword={keyword}
    />
  );
};

export default SearchUserContainer;
