import { useRouter } from "next/router";
import React, { useState, useCallback, useEffect } from "react";
import styled from "styled-components";
import searchOptions from "./search_options.json";

const Container = styled.div`
  width: 100%;
  margin: 0 auto;
  margin-top: 10px;
`;

const FilterWrapper = styled.span`
  ${props => props.theme.whiteBox};
  display: inline-block;
  padding: 10px;

  & + & {
    margin-left: 10px;
  }
`;

const setTag = (obj: any) =>
  Object.keys(obj)
    .filter(v => obj[v] && v !== "keyword")
    .map(v => ({
      query: v,
      value: searchOptions[v].filter(v2 => v2.value === obj[v]).join("")
    }));

const SearchTag = () => {
  const router = useRouter();
  const [filters, setFilters] = useState<any>(setTag(router.query));
  console.log(setTag(router.query));

  useEffect(() => {
    setFilters(setTag(router.query));
  }, [router.query]);

  return (
    <Container>
      {filters.map(v => (
        <FilterWrapper key={v.query}>
          {v.value}
          <span aria-hidden="true" style={{ marginLeft: 10 }}>
            Ã—
          </span>
        </FilterWrapper>
      ))}
    </Container>
  );
};

export default SearchTag;
