import { useRouter } from "next/router";
import React, { useState, useCallback, useEffect } from "react";
import styled from "styled-components";
import searchOptions from "./search_options";

const Container = styled.div`
  width: 100%;
  margin: 0 auto;
  margin-top: 10px;
`;

const FilterWrapper = styled.span`
  ${props => props.theme.whiteBox};
  display: inline-block;
  padding: 10px;

  & + & {
    margin-left: 10px;
  }
`;

const { sort, filter } = searchOptions;

const getValue = (obj: any) =>
  Object.keys(obj)
    .map(v => ({ query: v, value: obj[v] }))
    .filter(v => v.value);

const SearchTag = () => {
  const router = useRouter();
  const [filters, setFilters] = useState<any>(
    Object.keys(router.query)
      .map(v => ({ query: v, value: router.query[v] }))
      .filter(v => v.value)
  );

  useEffect(() => {
    setFilters(
      Object.keys(router.query)
        .map(v => ({ query: v, value: router.query[v] }))
        .filter(v => v.value)
    );
  }, [router.query]);

  return (
    <Container>
      {filters.map(v => (
        <FilterWrapper key={v.query}>
          {v.value}
          <span aria-hidden="true" style={{ marginLeft: 10 }}>
            Ã—
          </span>
        </FilterWrapper>
      ))}
    </Container>
  );
};

export default SearchTag;
