import { createGlobalStyle } from "styled-components";
import reset from "styled-reset";

export default createGlobalStyle`
  ${reset}

  * {
    box-sizing: border-box;
  }

  svg {
    cursor: pointer;

    &:hover {
      fill: #3897f0;
    }
  }



  .modal-dialog {
    position: relative;
    height: 100%;
    margin: 0 !important;
    margin-right: 0;
  }

  .modal-content {
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
  }
`;
