import React, { Fragment, FC } from "react";
import styled from "styled-components";
import { Modal, Button, ProgressBar } from "react-bootstrap";
import Input from "../common/Input";
import { Label } from "../common/Form";
import { UseInputProps } from "../../hooks";
import Loader from "../common/Loader";
import { Upload } from "../icon";

const UploadWrapper = styled.div`
  ${props => props.theme.whiteBox};
  background: ${props => props.theme.bgColor};
  width: 100%;
  height: 300px;
  cursor: pointer;
  padding: 10px;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const GuideLine = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;

  text-align: center;
  padding: 10px;

  & > div {
    flex: 1;
    padding: 10px;
  }
`;

type Props = {
  loading: boolean;
  header: string;
  title: UseInputProps;
  description: UseInputProps;
  status: UseInputProps;
  progress: number;
  file: string;
  fileEl: any;
  onClickFile: any;
  onChangeFile: any;
  onClose: any;
  onSubmit: any;
};

const SetNoticePresenter: FC<Props> = ({
  loading,
  header,
  title,
  description,
  status,
  progress,
  file,
  fileEl,
  onClickFile,
  onChangeFile,
  onClose,
  onSubmit
}) => (
  <Fragment>
    {loading && <Loader />}
    <Modal onHide={onClose} animation={true} show>
      <Modal.Header closeButton>
        <Modal.Title>{header}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {!file && (
          <UploadWrapper onClick={onClickFile}>
            <GuideLine>
              <div>
                {progress > 0 && (
                  <ProgressBar
                    now={progress}
                    label={`${progress}%`}
                    style={{ width: "100%" }}
                  />
                )}
              </div>
              <div>{progress === 0 && <Upload />}</div>
              <div>
                {progress === 0 && <span>영상을 업로드하려면 클릭하세요.</span>}
              </div>
              <div>
                {progress === 0 && <Button variant="primary">파일선택</Button>}
              </div>
            </GuideLine>

            <input
              type="file"
              onChange={onChangeFile}
              ref={fileEl}
              hidden
              accept="video/*"
            />
          </UploadWrapper>
        )}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="info" onClick={onClose}>
          취소
        </Button>
        {/* <Button variant="primary" onClick={onSubmit}>
              {action.code === "add" ? "등록" : "수정"}
            </Button> */}
      </Modal.Footer>
    </Modal>
  </Fragment>
);

export default SetNoticePresenter;
