import React, { FC } from "react";
import styled from "styled-components";
import { useQuery } from "@apollo/client";
import { userQuery } from "../../graphql/user/query";
import { useVssState } from "../../context";
import FollowButton from "../common/FollowButton";

const Container = styled.div`
  ${props => props.theme.whiteBox};
  position: absolute;
  display: flex;
  flex-direction: column;
  overflow: hidden;
  top: 35px;
  left: 0;
  width: 240px;
  height: 200px;
  z-index: 100;
`;

const Loading = styled.div`
  ${props => props.theme.whiteBox};
  position: absolute;
  overflow: hidden;
  display: flex;
  justfiy-content: flex-start;
  align-items: center;
  text-indent: 10px;
  top: 35px;
  left: 0;
  width: 240px;
  height: 50px;
  z-index: 100;
`;

const Background = styled.img`
  width: 100%;
  height: 150px;
`;

const Column = styled.div`
  display: flex;
  padding: 5px;
  justify-content: space-between;
  align-items: center;

  & > div {
    width: 50px;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
  }
`;

interface Props {
  userId: string;
}

const HoverUser: FC<Props> = ({ userId }) => {
  const { userId: MyUserId } = useVssState();
  const { data, loading } = useQuery(userQuery, {
    variables: {
      userId
    },
    fetchPolicy: "network-only"
  });

  if (loading) {
    return <Loading>로딩 중입니다...</Loading>;
  }
  const { nickname, avatar, followedBy } = data.getUser;
  const isFollowing = followedBy.some((v: any) => v.id === MyUserId);
  return (
    <Container>
      <Background src={avatar.url} />
      <Column>
        <div>{nickname}</div>
        <div>
          {userId !== MyUserId && (
            <FollowButton userId={userId} isFollowing={isFollowing} />
          )}
        </div>
      </Column>
    </Container>
  );
};

export default HoverUser;
