import React, { useCallback, FC, memo } from "react";
import styled from "styled-components";
import { useQuery } from "@apollo/client";
import { Carousel } from "react-bootstrap";
import { noticesQuery } from "../../graphql/notice/query";
import CarouselContainer from "../common/Carousel";
import Timestamp from "../common/Timestamp";
import { useVssState, useVssDispatch, SHOW_NOTICE_MODAL } from "../../context";
import NoData from "../common/NoData";

const NoticeWrapper = styled.div`
  height: 50px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin-right: 15%;
  margin-left: 15%;
  padding: 5px;
  text-align: center;
  cursor: pointer;
`;

const NoticeList: FC = () => {
  const dispatch = useVssDispatch();
  const { isMaster } = useVssState();

  const {
    data: { getNotices }
  } = useQuery(noticesQuery) as any;

  const handleClick = useCallback(
    (title, description, noticeId) => {
      dispatch({
        type: SHOW_NOTICE_MODAL,
        action: isMaster ? "modifiable" : "readonly",
        actionText: "",
        title,
        description,
        noticeId
      });
    },
    [isMaster]
  );

  const mapToNotices = () => {
    return getNotices.length > 0 ? (
      getNotices.map(({ id, title, description, updatedAt }) => (
        <Carousel.Item key={id}>
          <NoticeWrapper onClick={() => handleClick(title, description, id)}>
            <div>{title}</div>
            <Timestamp text={updatedAt} />
          </NoticeWrapper>
        </Carousel.Item>
      ))
    ) : (
      <NoData>공지사항이 없습니다.</NoData>
    );
  };
  return <CarouselContainer>{mapToNotices()}</CarouselContainer>;
};

export default memo(NoticeList);
