import { useRouter } from "next/router";
import React, { FC, Fragment } from "react";
import { useQuery } from "@apollo/client";
import { usersQuery } from "../../graphql/user/query";
import SearchUserPresenter from "./SearchUserPresenter";

const SearchUserContainer: FC = () => {
  const router = useRouter();
  const keyword = decodeURIComponent(router.query.keyword as string);

  const { data, loading } = useQuery(usersQuery, {
    variables: {
      keyword,
      first: 10
    }
  });

  if (!data && loading) {
    return <Fragment />;
  }

  return <SearchUserPresenter users={data.getUsers} keyword={keyword} />;
};

export default SearchUserContainer;
